    private void btnInformeProyectoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInformeProyectoActionPerformed
        // TODO add your handling code here:
        tablaInforme.setModel(modelo.modeloTablaProyectos());
        etiquetaInforme.setText("Informe basándose en la información de los proyectos"
                + " cuya clasificación sea Casa Campestre");
        activarBotonVaciarRegistro(true);
    }//GEN-LAST:event_btnInformeProyectoActionPerformed

    private void btnInformeLiderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInformeLiderActionPerformed
        // TODO add your handling code here:
        tablaInforme.setModel(modelo.modeloTablaLideres());
        etiquetaInforme.setText("Informe con la información respectiva al Líder");
        activarBotonVaciarRegistro(true);
        
        
    }//GEN-LAST:event_btnInformeLiderActionPerformed

    private void btnInformeCompraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInformeCompraActionPerformed
        // TODO add your handling code here:
        tablaInforme.setModel(modelo.modeloTablaCompras());
        etiquetaInforme.setText("Informe basándose en las compras realizadas por los proyectos con el proveedor Homecenter"
                + " y para la ciudad Salento");
        activarBotonVaciarRegistro(true);
    }//GEN-LAST:event_btnInformeCompraActionPerformed

    private void btnLimpiarTablaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimpiarTablaActionPerformed
        // TODO add your handling code here:
        limpiarModeloTabla();
        etiquetaInforme.setText("");
        activarBotonVaciarRegistro(false);
    }//GEN-LAST:event_btnLimpiarTablaActionPerformed

    
