    // Método para activar y desactivar el boton vaciar tabla
    private void activarBotonVaciarRegistro(boolean flag){
        if (flag) {
            btnLimpiarTabla.setEnabled(flag);
        } else {
            btnLimpiarTabla.setEnabled(flag);
        }
    }
    
    // Método para vaciar los registros del modelo la tabla
    private void limpiarModeloTabla(){
        modelo.getModeloTabla().setColumnCount(0);
        modelo.getModeloTabla().setRowCount(0);
    }
